import React, { useState } from 'react';

import MaterialTable from 'material-table'

import GetAppIcon from '@material-ui/icons/GetApp';
import AddIcon from '@material-ui/icons/Add';
import Contacts from '../../flower'
function Table() {

  const [tableData, setTableData] = useState(Contacts)
  const columns = [
    { title: "Gene", field: "Gene", sorting: true, filtering: true, cellStyle: { background:"#009688" }, headerStyle: { color: "#fff" } },
    { title: "Variant", field: "Variant", filterPlaceholder: "filter" },
    { title: "Impact", field: "Impact", align: "center", filterPlaceholder:"filter" },


    { title: "Protein_Effect", field: "Protein_Effect",filterPlaceholder:"filter" },
    { title: "Variant_Description", field: "Variant_Description", filterPlaceholder:"filter" },
    { title: "Associated_with_drug_Resistance", field: "Associated_with_drug_Resistance", filterPlaceholder:"filter" },



  ]

  return (

     <div className='container'>



      <h1 align="center">Gene Data</h1>


      <MaterialTable 
      columns={columns}
       data={tableData}

        editable={{
          onRowAdd: (newRow) => new Promise((resolve, reject) => {
            setTableData([...tableData, newRow])

            setTimeout(() => resolve(), 500)
          }),
          onRowUpdate: (newRow, oldRow) => new Promise((resolve, reject) => {
            const updatedData = [...tableData]
            updatedData[oldRow.tableData.id] = newRow
            setTableData(updatedData)
            setTimeout(() => resolve(), 500)
          }),
          onRowDelete: (selectedRow) => new Promise((resolve, reject) => {
            const updatedData = [...tableData]
            updatedData.splice(selectedRow.tableData.id, 1)
            setTableData(updatedData)
            setTimeout(() => resolve(), 1000)

          })
        }}
        actions={[
          {
            icon: () => <GetAppIcon />,
            tooltip: "Click me",
            onClick: (e, data) => console.log(data),

          }
        ]}
        onSelectionChange={(selectedRows) => console.log(selectedRows)}
        options={{
          sorting: true, search: true,
          searchFieldAlignment: "right", searchAutoFocus: true, searchFieldVariant: "standard",
          filtering: true, paging: true, pageSizeOptions: [2, 5, 10, 20], pageSize: 5,
          paginationType: "stepped", showFirstLastPageButtons: false, paginationPosition: "both", exportButton: true,
          exportAllData: true, exportFileName: "TableData", addRowPosition: "first", actionsColumnIndex: -1, selection: true,
          showSelectAllCheckbox: false, showTextRowsSelected: false, selectionProps: rowData => ({
            disabled: rowData.age == null,

          }),
          grouping: true, columnsButton: true,
          rowStyle: (data, index) => index % 2 === 0 ? { background: "#f5f5f5" } : null,
          headerStyle: { background: "#f44336",color:"#fff"}
        }}
        title="Gene Information"
        icons={{ Add: () => <AddIcon /> }}







        />


</div>



  );
}

export default Table;
